#CIRCOS CONFIGURATION
#non optional requirements 
#1.) Required are Karyotype
#2.) 2D plot which details the synteny between bases 
show_histogram = yes
show_scatter = yes
show_line = yes
show_heatmap = yes
show_tile = yes
show_highlight = yes
use_rules = yes
show_label = yes
show_ticks = yes
show_tick_labels = yes
chromosomes_units = 5000000

#Karyotype required this is a file which details chromosome lengths this is 
karyotype = /home/jconnell/projects/niab/johnc/final_karyotype.txt

#Set spacing between contigs 

<ideogram>

  <spacing>

    # spacing between ideograms is 0.5% of the image

    #default = 0.005r

    default = 0.001r

    <pairwise Scaffold_191 Scaffold_49>

      # spacing between contig1 and FoL_chromosome_1 is 4x of 0.5% (2%) of image

      # The angle of the ideogram is also edited in <image> below.

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_1531 Scaffold_19>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_1533 Scaffold_19>

      #spacing = 0.005r

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_1533 Scaffold_76>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_76 Scaffold_24>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_24 Scaffold_172>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_172 Scaffold_77>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_77 Scaffold_73>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_73 Scaffold_49>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_49 Scaffold_191>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_191 Scaffold_110>

      spacing = 50r

    </pairwise>

    <pairwise Scaffold_1531 Scaffold_1522>

      spacing = 50r

    </pairwise>

  </spacing>



  # ideogram position, thickness and fill

  radius           = 0.90r
  thickness        = 40p
  fill             = yes
  stroke_thickness = 0.5
  stroke_color     = chr6



  # ideogram labels

  # <<include ideogram.label.conf>>

  show_label        = no



  # show labels only for contigs 1-16 and

  # use the chromosome name as the label, but replace "contig" with "FoC"

  #label_format     = eval( var(idx) < 16? replace(var(chr),"contig_","Cont_") : "")
  label_format   = eval(sprintf("chr%s",var(label)))

  label_color    = gneg

  # 5% of inner radius outside outer ideogram radius

  label_radius = dims(ideogram,radius_outer) + 0.15r

  label_size        = 30p

  label_font        = bold

  label_parallel    = yes



  # ideogram cytogenetic bands, if defined in the karyotype file

  # <<include bands.conf>>

</ideogram>

<background>
color = gpos
</background>


<image>

  # override the default angle_offset of -90 defined in etc/image.conf

  angle_offset* = -90

  radius* = 1100p

  <<include etc/image.conf>>  # included from Circos distribution



